import { Button, MenuItem, Select } from "@mui/material";
import { useEffect, useState } from "react";

function List() {
  const [universities, setUniversities] = useState([]);
  const [selectedUniversity, setSelectedUniversity] = useState("");

  useEffect(() => {
    fetch("https://unilinkapiv1.azurewebsites.net/api/UniversityFilter")
      .then((response) => response.json())
      .then((data) => setUniversities(data))
      .catch((error) => console.error("Error fetching universities:", error));
  }, []);

  return (
    <div className="w-full max-w-[1920px] mx-auto">
      <div className="p-4 border rounded-lg shadow-lg m-10">
        <div className="m-2">
          <div className="grid grid-cols-12 gap-4">
            <div className="col-span-4">
              <label htmlFor="dropdown1">Select University</label>
              <Select
                id="dropdown1"
                fullWidth
                variant="outlined"
                value={selectedUniversity}
                onChange={(event) => setSelectedUniversity(event.target.value)}
              >
                <MenuItem value="">Select University</MenuItem>
                {universities.map((uni) => (
                  <MenuItem key={uni.id} value={uni.universityName}>
                    {uni.universityName}
                  </MenuItem>
                ))}
              </Select>
            </div>
            {/* Add another dropdown for Program selection if needed */}
          </div>
        </div>
      </div>

      {/* Display university details based on selection */}
      {selectedUniversity && (
        <div className="p-4 border rounded-lg shadow-lg m-10">
          <div className="m-2 flex flex-col">
            {/* Your code to display selected university details */}
          </div>
        </div>
      )}
    </div>
  );
}

export default List;
