import { useState, useEffect } from "react";
import { Button } from "@mui/material";

function List() {
  const [universitiesData, setUniversitiesData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const universitiesList = [
        // Universities list with APIs
      ];

      const finalArray = await Promise.all(
        universitiesList.map(async (uni) => {
          const uniData = { name: uni.name, department: {}, fee: {} };
          await Promise.all(
            uni.apis.map(async (api, index) => {
              const response = await fetch(api);
              const data = await response.json();
              // Determine the type of data fetched (departments or fees)
              if (api.includes("Departments")) {
                // Store only the first department
                uniData.department = data[0];
              } else if (api.includes("Fees")) {
                // Store fee data
                uniData.fee = data[0];
              }
            })
          );
          // Return the uniData object for this university
          return uniData;
        })
      );

      // Set the final array of universities data
      setUniversitiesData(finalArray);
    };

    fetchData();
  }, []);

  return (
    <div className="w-full max-w-[1920px] mx-auto">
      <div className="p-4 border rounded-lg shadow-lg m-10">
        <div className="m-2 flex flex-col">
          {universitiesData?.map((uni, index) => (
            <div
              key={index}
              className="border p-4 mb-4 rounded-lg flex flex-col gap-7 shadow-lg"
            >
              <h2 className="text-[#3579F8] font-medium text-xl mb-4">
                {uni.name}
              </h2>
              <div>
                <h3>Department:</h3>
                {/* Display only the first department */}
                <div>{uni.department.departmentName}</div>
              </div>
              <div>
                <h3>Fees:</h3>
                {/* Display fee data */}
                <div>{uni.fee.admissionFee}</div>
              </div>
              <div className="flex gap-2">
                <Button variant="contained" size="large">
                  View University
                </Button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default List;
