import React from "react";

function DiscussionForum() {
  const questions = [
    {
      question: "Who is Ro for?",
      answer:
        "Ro is for adults who are experiencing health issues or want to improve or support their health and prefer to do so from the comfort of their home.",
    },
    {
      question: "Strategic partnerships with leading blockchain companies",
      answer:
        "Ro is for adults who are experiencing health issues or want to improve or support their health and prefer to do so from the comfort of their home.",
    },
    {
      question: "Early-stage fundraising for Metaverse-bound projects.",
      answer:
        "Ro is for adults who are experiencing health issues or want to improve or support their health and prefer to do so from the comfort of their home.",
    },
    {
      question: "Primary market prospects with a growing audience base.",
      answer:
        "Ro is for adults who are experiencing health issues or want to improve or support their health and prefer to do so from the comfort of their home.",
    },
  ];

  return (
    <div className="max-w-[1920px] mx-auto">
      <div className="grid grid-cols-12 p-6">
        <div className="col-span-5">
          <img
            src="assets/img/question.png"
            className="h-[798px] w-[579px]"
            alt=""
          />
        </div>
        <div className="col-span-7">
          <h2 className="title-font">Frequently asked question</h2>
          {questions.map((question, index) => (
            <div key={index} className="flex flex-col">
              <div className="font-medium text-black border-b border-[#E6E6E6]">
                <h2 className="">
                  {question.question}
                  <span className="flex justify-end w-full">
                    <span className="transition-all duration-100 ease-linear">
                      <img src="images/question.png" alt="" />
                    </span>
                  </span>
                </h2>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default DiscussionForum;
